from torch.utils.data import DataLoaderfrom . import trainerfrom ..data import SegmentationDatasetfrom ..config import *from ..model import SegmentationNetworktrain_dataset = SegmentationDataset(DATA_DIR + 'train')val_dataset = SegmentationDataset(DATA_DIR + 'validation')train_loader = DataLoader(train_dataset,                          batch_size=BATCH_SIZE,                          num_workers=NUM_WORKERS,                          pin_memory=True,                          drop_last=True,                          shuffle=True)val_loader = DataLoader(val_dataset,                        batch_size=BATCH_SIZE,                        num_workers=NUM_WORKERS,                        pin_memory=True,                        drop_last=True,                        shuffle=False)net = SegmentationNetwork()if __name__ == '__main__':    trainer.fit(net, train_dataloader=train_loader, val_dataloaders=val_loader)